t type="checkbox" id="remember">                          <label for="remember">                            Remember Me                          </label>                        </div>                      </div>                      <!-- /.col -->                      <div class="col-4">                        <button type="button" class="btn btn-primary btn-block" id="btn-loginUser">Sign In</button>                      </div>                      <!-- /.col -->                    </div>                  </form>                  <!-- <div class="social-auth-links text-center mt-2 mb-3">                    <a href="#" class="btn btn-block btn-primary">                      <i class="fab fa-facebook mr-2"></i> Sign in using Facebook                    </a>                    <a href="#" class="btn btn-block btn-danger">                      <i class="fab fa-google-plus mr-2"></i> Sign in using Google+                    </a>                  </div> -->                  <!-- /.social-auth-links -->                  <!-- <p class="mb-1">                    <a href="forgot-password.html">I forgot my password</a>                  </p> -->                  <p class="mb-0">                    <a href="<?php echo base_url('pendaftaran'); ?>" class="text-center">Register a new membership</a>                  </p>                </div>                <!-- /.card-body -->              </div>              <!-- /.card -->            </div>            <!-- /.login-box -->        </div>    </section>   <?php $this->load->view('layouts_users/footer2'); ?>   <script>    $(document).ready(function() {        //Show Password        $('#show-password').on('click', function(){            if ($(this).hasClass('fa-eye')) {                $('#password').attr('type', 'text');                $(this).removeClass('fa-eye');                $(this).addClass('fa-eye-slash');            } else {                $('#password').attr('type', 'password');                $(this).removeClass('fa-eye-slash');                $(this).addClass('fa-eye');            }        });        //Submit password admin        $('#btn-loginUser').on('click', function(){          const formLogin = $('#formLoginUser');                    $.ajax({            url: "<?php echo base_url('login/cekuser')?>",            method: "POST",            data: formLogin.serialize(),            dataType: "JSON",            success: function (data) {              //Data Error              if(data.error){                if(data.email_error != '') $('#email_error').html(data.email_error);                   else $('#email_error').html('');                if(data.password_error != '') $('#password_error').html(data.password_error);                   else $('#password_error').html('');                Swal.fire({                  icon: 'warning',                  title: 'Oops...',                  text: 'Check again your data!',                });              }              //Login Sukses              if(data.success){                 Swal.fire({                  icon: 'success',                  title: 'ERP Ngemper',                  text: 'Login Success!',                });                window.location.replace(data.link);              }                            }                      });        });    });   </script></body></html>